    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Mot de passe oublié{% endblock %}

{% block body %}

<div class=\"col-sm-12 col-md-12 col-lg-12 row1\">
    {% for flashError in app.flashes('reset_password_error') %}
        <div class=\"alert alert-danger\" role=\"alert\">{{ flashError }}</div>
    {% endfor %}
    <h3 class=\"col-12 text-center\">Réinitialiser votre mot de passe</h3>

    <div class=\"row justify-content-center\">
        <div class=\"col-sm-12 col-md-8 col-lg-8\" >
        {{ form_start(requestForm) }}
            {{ form_row(requestForm.email, {'attr':{'class':'form-control'}}) }}
            <div>
                <small>
                    Entrez votre adresse email, nous vous enverrons un lien afin de modifier votre mot de passe.
                </small>
            </div>
            <div class=\"col-12 text-center\">
                <button class=\"btn btn-primary\">Envoyer l'email de réinitialisation</button>
            </div>
        {{ form_end(requestForm) }}
        </div>
    </div>

</div>
{% endblock %}", "reset_password/request.html.twig", "/Applications/MAMP/htdocs/fdain/fdain/templates/reset_password/request.html.twig");
